import React, { useCallback } from 'react';
import styled from 'styled-components';
import { keyframes } from '@emotion/react';

// import { AiOutlineClose } from 'react-icons/ai';

export const Modal = ({ children, isOpen, setIsOpen }) => {
  const onClickExit = useCallback(
    (e) => {
      if (!isOpen) return;

      setIsOpen((isOpen) => !isOpen);
    },
    [isOpen, setIsOpen]
  );

  return (
    <>
      {isOpen && (
        <ModalPage>
          <ModalWrapper>
            <ModalForm>
              <ModalExitWrapper>
                {/* <ModalExit size={'1.5rem'} onClick={onClickExit} /> */}
              </ModalExitWrapper>
              {children}
            </ModalForm>
          </ModalWrapper>
        </ModalPage>
      )}
    </>
  );
};

const ModalPage = styled.div`
  width: 100vw;
  height: 100vh;

  position: fixed;

  display: flex;
  top: 0;
  left: 0;

  justify-content: center;

  align-items: center;

  z-index: 100;

  background-color: rgba(255, 255, 255, 0.4);
  box-shadow: 30px 30px 30px #414141;
`;

const ModalAnimation = keyframes`
    0%{
        opacity : 0;
        transform : translateY(400px) scale(0.75);
    },
    75%{
        opacity : 1;
        transform : translateY(-16%) scale(1);
    }
    100%{
        opacity : 1;
        transform : translateY(0);
    }
`;

const ModalWrapper = styled.div`
  width: 606px;
  height: 480px;

  animation: 0.4s ease-in-out 0s 1 normal forwards running ${ModalAnimation};
  box-shadow: rgb(0 0 0 / 9%) 0px 2px 12px 0px;
  display: flex;
`;

const ModalForm = styled.div`
  border: 10px solid red;
  width: 100%;
  height: 100%;
  padding: 1.5rem;

  display: flex;
  flex-direction: column !important;
  flex-wrap: wrap;

  background-color: white;
`;

const ModalExitWrapper = styled.div`
  margin-bottom: 2.25rem;

  display: flex;
  justify-content: flex-end;
  -webkit-box-pack: end;
`;

// const ModalExit = styled(AiOutlineClose)`
